import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

const CricketNews = () => {
  const newsItems = [
    {
      title: 'Cricket World Cup 2024 Begins',
      description: 'The Cricket World Cup 2024 kicks off with exciting matches and high anticipation among fans worldwide.',
      image: 'https://example.com/image1.jpg', // Replace with your image URL
      link: 'https://example.com/news1' // Replace with your news article link
    },
    {
      title: 'New Record Set in T20 League',
      description: 'A new record was set in the T20 League as a player scores the fastest century of the season.',
      image: 'https://example.com/image2.jpg', // Replace with your image URL
      link: 'https://example.com/news2' // Replace with your news article link
    },
    {
      title: 'Upcoming Test Series Announcement',
      description: 'The schedule for the upcoming Test series has been announced with thrilling matches lined up.',
      image: 'https://example.com/image3.jpg', // Replace with your image URL
      link: 'https://example.com/news3' // Replace with your news article link
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to handle manual navigation to the next news item
  const nextNewsItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % newsItems.length);
  };

  // Auto-scroll functionality using useEffect
  useEffect(() => {
    const interval = setInterval(() => {
      nextNewsItem();
    }, 5000); // Automatically switch news every 5 seconds

    // Clean up the interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return (
    <section className="py-8 px-4 sm:px-6 lg:px-12">
      <h2 className="font-semibold text-center text-[#316685]" style={{ fontSize: '2rem' }}>Cricket News</h2>
      
      <div className="relative">
        {/* Motion div for the current news item */}
        <motion.div
          key={currentIndex} // Ensures the animation restarts with new content
          style={{
            backgroundColor: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '16px',
            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
            width: '100%'
          }}
          variants={itemVariants}
          initial="hidden"
          animate="visible"
          transition={{ duration: 0.5 }}
        >
          <img
            src={newsItems[currentIndex].image}
            alt={newsItems[currentIndex].title}
            style={{
              width: '100%',
              height: '160px',
              objectFit: 'cover',
              borderRadius: '8px',
              marginBottom: '16px'
            }}
          />
          <h3 className="text-lg font-semibold mb-2" style={{
            fontSize: '1.25rem',
            marginBottom: '8px'
          }}>{newsItems[currentIndex].title}</h3>
          <p className="text-gray-600 mb-4" style={{
            fontSize: '1rem',
            marginBottom: '16px'
          }}>{newsItems[currentIndex].description}</p>
          <a href={newsItems[currentIndex].link} style={{
            fontSize: '1rem',
            textDecoration: 'none',
            color: '#007bff'
          }}>Read More</a>
        </motion.div>

        {/* Next button for manual navigation */}
        <button
          onClick={nextNewsItem}
          style={{
            position: 'absolute',
            top: '50%',
            right: '10px',
            backgroundColor: '#316685',
            color: '#fff',
            borderRadius: '50%',
            padding: '8px 12px',
            cursor: 'pointer',
            transform: 'translateY(-50%)'
          }}
        >
          Next
        </button>
      </div>
    </section>
  );
};

export default CricketNews;
