import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

const CricketNews = () => {
  const newsItems = [
    {
      title: 'Cricket World Cup 2024 Begins',
      description: 'The Cricket World Cup 2024 kicks off with exciting matches and high anticipation among fans worldwide.',
      image: 'https://img.jagranjosh.com/images/2023/September/2092023/ICC-T20-World-Cup-2024.webp', // Replace with your image URL
      link: 'https://www.business-standard.com/cricket/icc-t20-world-cup/t20-world-cup-2024-full-schedule-format-match-time-ist-live-streaming-124053100763_1.html' // Replace with your news article link
    },
    {
      title: 'New Record Set in T20 League',
      description: 'A new record was set in the T20 League as a player scores the fastest century of the season.',
      image: 'https://static.toiimg.com/thumb/msid-104256760,width-1280,height-720,resizemode-4/104256760.jpg', // Replace with your image URL
      link: 'https://timesofindia.indiatimes.com/sports/cricket/news/aussie-batter-breaks-ab-de-villiers-record-slams-fastest-ever-hundred-in-list-a-cricket/articleshow/104256694.cms' // Replace with your news article link
    },
    {
      title: 'Upcoming Test Series Announcement',
      description: 'The schedule for the upcoming Test series has been announced with thrilling matches lined up.',
      image: 'https://example.com/image3.jpg', // Replace with your image URL
      link: 'https://www.bcci.tv/articles/2024/news/55556143/india-s-squad-for-the-1st-test-of-the-idfc-first-bank-test-series-against-bangladesh-announced' // Replace with your news article link
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to handle manual navigation to the next news item
  const nextNewsItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % newsItems.length);
  };

  // Function to handle manual navigation to the previous news item
  const prevNewsItem = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? newsItems.length - 1 : prevIndex - 1
    );
  };

  // Auto-scroll functionality using useEffect
  useEffect(() => {
    const interval = setInterval(() => {
      nextNewsItem();
    }, 5000); // Automatically switch news every 5 seconds

    // Clean up the interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return (
    <section className="py-8 px-4 sm:px-6 lg:px-12">
      <h2 className="font-semibold text-center text-[#316685]" style={{ fontSize: '2rem' }}>Cricket News</h2>
      
      <div className="relative">
        {/* Motion div for the current news item */}
        <motion.div
          key={currentIndex} // Ensures the animation restarts with new content
          style={{
            backgroundColor: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '16px',
            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
            width: '100%'
          }}
          variants={itemVariants}
          initial="hidden"
          animate="visible"
          transition={{ duration: 0.5 }}
        >
          <img
            src={newsItems[currentIndex].image}
            alt={newsItems[currentIndex].title}
            style={{
              width: '100%',
              height: '160px',
              objectFit: 'cover',
              borderRadius: '8px',
              marginBottom: '16px'
            }}
          />
          <h3 className="text-lg font-semibold mb-2" style={{
            fontSize: '1.25rem',
            marginBottom: '8px'
          }}>{newsItems[currentIndex].title}</h3>
          <p className="text-gray-600 mb-4" style={{
            fontSize: '1rem',
            marginBottom: '16px'
          }}>{newsItems[currentIndex].description}</p>
          <a href={newsItems[currentIndex].link} style={{
            fontSize: '1rem',
            textDecoration: 'none',
            color: '#007bff'
          }}>Read More</a>
        </motion.div>

        {/* Previous button */}
        <button
          onClick={prevNewsItem}
          className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-[#316685] text-white rounded-full p-3"
          style={{
            fontSize: '1.5rem',
            cursor: 'pointer'
          }}
        >
          &#8592; {/* Left arrow */}
        </button>

        {/* Next button */}
        <button
          onClick={nextNewsItem}
          className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-[#316685] text-white rounded-full p-3"
          style={{
            fontSize: '1.5rem',
            cursor: 'pointer'
          }}
        >
          &#8594; {/* Right arrow */}
        </button>
      </div>
    </section>
  );
};

export default CricketNews;
