"use client";

import React from "react";
import { CardBody, CardContainer, CardItem } from "../ui/3d-card";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

// 3D Card Component
export function ThreeDCardDemo() {
  return (
    <CardContainer className="inter-var">
      <CardBody className="bg-gray-50 relative group/card dark:hover:shadow-2xl dark:hover:shadow-emerald-500/[0.1] dark:bg-black dark:border-white/[0.2] border-black/[0.1] w-auto sm:w-[30rem] h-auto rounded-xl p-6 border">
        <CardItem
          translateZ="50"
          className="text-xl font-bold text-neutral-600 dark:text-white"
        >
          Make things float in air
        </CardItem>
        <CardItem
          as="p"
          translateZ="60"
          className="text-neutral-500 text-sm max-w-sm mt-2 dark:text-neutral-300"
        >
          Hover over this card to unleash the power of CSS perspective
        </CardItem>
        <CardItem translateZ="100" className="w-full mt-4">
          <img
            src="https://images.unsplash.com/photo-1441974231531-c6227db76b6e?q=80&w=2560&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
            className="h-60 w-full object-cover rounded-xl group-hover/card:shadow-xl"
            alt="thumbnail"
          />
        </CardItem>
        <div className="flex justify-between items-center mt-20">
          <CardItem
            translateZ={20}
            as={Link}
            to="https://twitter.com/mannupaaji"
            target="__blank"
            className="px-4 py-2 rounded-xl text-xs font-normal dark:text-white"
          >
            Try now â†’
          </CardItem>
          <CardItem
            translateZ={20}
            as="button"
            className="px-4 py-2 rounded-xl bg-black dark:bg-white dark:text-black text-white text-xs font-bold"
          >
            Sign up
          </CardItem>
        </div>
      </CardBody>
    </CardContainer>
  );
}

// Featured Teams Component
const FeaturedTeams = () => {
  const teams = [
    {
      name: "Team A",
      description: "Description for Team A",
      logo: "https://www.shutterstock.com/image-vector/cricket-sport-team-club-logo-600nw-2156607285.jpg",
      points: 120,
    },
    {
      name: "Team B",
      description: "Description for Team B",
      logo: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRfObvcZio7M90JvyCWytrh7McQu_m8J0j1Bg&s",
      points: 95,
    },
    {
      name: "Team C",
      description: "Description for Team C",
      logo: "https://t4.ftcdn.net/jpg/04/26/13/71/360_F_426137121_u2zwsANOFCMcAZyAKAA9jJu1GCWxWaLG.jpg",
      points: 88,
    },
    {
      name: "Team D",
      description: "Description for Team D",
      logo: "https://w7.pngwing.com/pngs/812/694/png-transparent-logo-sports-association-cricket-team-cricket-purple-text-sport.png",
      points: 110,
    },
  ];

  return (
    <section className="py-8 px-4 sm:px-6 lg:px-12">
      <h2
        className="font-semibold text-center text-[#316685] mb-2"
        style={{ fontSize: "36px" }}
      >
        Featured Teams
      </h2>
      <p className="text-base sm:text-lg md:text-xl text-[#4B5563] mb-8 leading-relaxed">
        {/* Increased margin-bottom */}
        The teams with highest points scored all over the seasons
      </p>
      <div className="flex flex-wrap justify-center gap-6">
        {teams.map((team, index) => (
          <motion.div
            key={team.name}
            className="bg-white border border-gray-200 rounded-lg shadow-lg p-3 sm:p-4 md:p-5 lg:p-6 text-center flex flex-col items-center w-64"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            whileHover={{ scale: 1.05 }}
          >
            <img
              src={team.logo}
              alt={`${team.name} Logo`}
              className="w-16 h-16 sm:w-20 sm:h-20 md:w-24 md:h-24 lg:w-28 lg:h-28 mb-4 object-cover"
            />
            <h3 className="text-lg sm:text-xl md:text-2xl font-semibold mb-2">
              {team.name}
            </h3>
            <p className="text-gray-600 text-sm sm:text-base md:text-lg mb-2">
              {team.description}
            </p>
            <p className="text-blue-500 text-sm sm:text-base md:text-lg font-semibold">
              Total Points: {team.points}
            </p>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default FeaturedTeams;
