import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";
import { HamburgetMenuClose, HamburgetMenuOpen } from "./Icons";

function NavBar() {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);

  return (
    <nav className="navbar mb-0 pb-0">
      <div className="nav-container">
        <NavLink to="/" className="nav-logo">
          <span>CRICKO</span>
          <img src="https://images.vexels.com/media/users/3/316363/isolated/preview/02c4382e5d08daec77666410ddae3e6a-cricket-bat.png" alt="Cricket Bat" className="bat-image" />
        </NavLink>

        <ul className={click ? "nav-menu active" : "nav-menu"}>
          <li className="nav-item">
            <NavLink
              to="/"
              className={({ isActive }) => 
                isActive ? "nav-links active" : "nav-links"
              }
              onClick={handleClick}
            >
              Home
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink
              to="/about"
              className={({ isActive }) => 
                isActive ? "nav-links active" : "nav-links"
              }
              onClick={handleClick}
            >
              Tournaments
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink
              to="/blog"
              className={({ isActive }) => 
                isActive ? "nav-links active" : "nav-links"
              }
              onClick={handleClick}
            >
              Teams
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink
              to="/contact"
              className={({ isActive }) => 
                isActive ? "nav-links active" : "nav-links"
              }
              onClick={handleClick}
            >
              About
            </NavLink>
          </li>
        </ul>
        <div className="nav-icon" onClick={handleClick}>
          {click ? (
            <span className="icon">
              <HamburgetMenuClose /> 
            </span>
          ) : (
            <span className="icon">
              <HamburgetMenuOpen />
            </span>
          )}
        </div>
      </div>
    </nav>
  );
}

export default NavBar;
