import React from 'react';
import './TournamentsPage.css';

// Sample tournaments with random placeholder logos
const tournaments = [
  {
    id: 1,
    name: 'World T20 Championship',
    format: 'T20',
    date: '2023-10-10',
    completed: true,
    logo: 'https://via.placeholder.com/150x150.png?text=T20+Championship',
  },
  {
    id: 2,
    name: 'ODI World Cup',
    format: 'ODI',
    date: '2024-03-15',
    completed: false,
    logo: 'https://via.placeholder.com/150x150.png?text=ODI+World+Cup',
  },
  {
    id: 3,
    name: 'The Ashes',
    format: 'Test',
    date: '2024-07-05',
    completed: false,
    logo: 'https://via.placeholder.com/150x150.png?text=The+Ashes',
  },
  {
    id: 4,
    name: 'Asia Cup',
    format: 'ODI',
    date: '2023-08-01',
    completed: true,
    logo: 'https://via.placeholder.com/150x150.png?text=Asia+Cup',
  },
];

const TournamentsPage = () => {
  return (
    <div className="tournaments-page">
      <h1 className="heading">Cricket Tournaments</h1>
      <div className="tournament-list">
        {tournaments.map((tournament) => (
          <div
            key={tournament.id}
            className={`tournament-card horizontal ${tournament.completed ? 'completed' : ''}`}
          >
            <img src={tournament.logo} alt={`${tournament.name} logo`} className="tournament-logo" />
            <div className="tournament-info">
              <h2 className="tournament-name">{tournament.name}</h2>
              <p>Format: {tournament.format}</p>
              <p>Date: {tournament.date}</p>
            </div>
            <button
              className="register-btn"
              disabled={tournament.completed}
            >
              {tournament.completed ? 'Completed' : 'Register'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TournamentsPage;
