import React, { useState, useEffect } from 'react';
import ScoreCard from '../HomeCompo/ScoreCard';
//import PlayerList from '../HomeCompo/PlayerList'; // Adjust import if necessary
import PlayerList from '../HomeCompo/PlayerList';

export default function ClientPanel() {
  const [scoreData, setScoreData] = useState({
    homeTeam: 'Team A',
    awayTeam: 'Team B',
    homeScore: 0,
    homeWickets: 0,
    awayScore: 0,
    awayWickets: 0,
    overs: 0,
    homePlayers: Array(11).fill({ name: 'Player', runs: 0, balls: 0, fours: 0, sixes: 0 }),
    awayPlayers: Array(11).fill({ name: 'Player', runs: 0, balls: 0, fours: 0, sixes: 0 }),
    homeBowlers: Array(11).fill({ name: 'Bowler', overs: 0, maiden: 0, runsGiven: 0, wickets: 0 }),
    awayBowlers: Array(11).fill({ name: 'Bowler', overs: 0, maiden: 0, runsGiven: 0, wickets: 0 })
  });

  useEffect(() => {
    const fetchScoreData = async () => {
      // Simulate fetching data
      const response = await fetch('/api/score');
      const data = await response.json();
      setScoreData(data);
    };

    fetchScoreData();
    const interval = setInterval(fetchScoreData, 5000); // Update every 5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold mb-8">Cricket Live Score</h1>
      <div className="max-w-4xl mx-auto">
        <ScoreCard
          homeTeam={scoreData.homeTeam}
          awayTeam={scoreData.awayTeam}
          homeScore={scoreData.homeScore}
          homeWickets={scoreData.homeWickets}
          awayScore={scoreData.awayScore}
          awayWickets={scoreData.awayWickets}
          overs={scoreData.overs}
        />
        <PlayerList players={scoreData.homePlayers} title={`${scoreData.homeTeam} Players`} />
        <PlayerList players={scoreData.awayPlayers} title={`${scoreData.awayTeam} Players`} />
        <PlayerList players={scoreData.homeBowlers} title={`${scoreData.homeTeam} Bowlers`} isBowler={true} />
        <PlayerList players={scoreData.awayBowlers} title={`${scoreData.awayTeam} Bowlers`} isBowler={true} />
      </div>
    </div>
  );
}
