const itemVariants = {
  visible: { opacity: 1, y: 0, transition: { type: "spring", stiffness: 120, duration: 0.8 } },
  hidden: { opacity: 0, y: 50 }, // Paragraphs slide in from below
};

const HeroSection = () => {
  return (
    <motion.section
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className="relative text-gray-600 body-font flex items-center justify-center bg-cover bg-center h-[70vh] md:h-[65vh] lg:h-[60vh]" // Adjusted image height
      style={{
        backgroundImage:
          "url('https://www.fabhotels.com/blog/wp-content/uploads/2023/04/adelaide-oval.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="absolute inset-0 bg-black bg-opacity-60"></div> {/* Darkened overlay for better text visibility */}
      <div className="relative container mx-auto px-4 py-4 md:py-8 flex flex-col items-center text-center">
        <motion.div variants={itemVariants} className="flex flex-col items-center mb-8">

          {/* Animate the first paragraph */}
          <motion.p
            variants={itemVariants}
            initial="hidden"
            animate="visible"
            className="text-neutral-200 text-lg md:text-xl lg:text-2xl font-semibold mb-4"
          >
            Experience the Thrill of Cricket with CRICKO Organization
          </motion.p>

          {/* Typewriter animation text */}
          <div className="text-white text-lg md:text-2xl lg:text-3xl font-bold mb-4">
            <TypewriterEffectDemo />
          </div>

          {/* Animate the second paragraph */}
          <motion.p
            variants={itemVariants}
            initial="hidden"
            animate="visible"
            className="text-white text-sm sm:text-md md:text-lg leading-relaxed max-w-2xl mb-6"
          >
            Join us for exciting cricket tournaments in various formats. Whether you're a player or a fan, we've got something for everyone. Discover the passion, skill, and excitement of cricket with CRICKO Organization.
          </motion.p>

          <div className="flex justify-center space-x-4 mt-6">
            <Link to="/about">
              <motion.button
                whileHover="hover"
                variants={buttonVariants}
                className="inline-flex text-white bg-custom-blue border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-custom-blue/80 rounded"
              >
                Explore Tournaments
              </motion.button>
            </Link>
            <Link to="/contact">
              <motion.button
                whileHover="hover"
                variants={buttonVariants}
                className="inline-flex text-gray-700 bg-gray-100 border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-gray-200 rounded"
              >
                About Us
              </motion.button>
            </Link>
          </div>
        </motion.div>
      </div>
    </motion.section>
  );
};
