import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";
import { HamburgetMenuClose, HamburgetMenuOpen } from "./Icons";
import CricketBallIcon from "./ui/CricketBallIcon";
import CricketBatLogo from "./ui/CricketBatLogo";

const NavBar = () => {
  const [navMenuOpen, setNavMenuOpen] = useState(false);

  const handleNavMenuToggle = () => {
    setNavMenuOpen(!navMenuOpen);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <NavLink to="/" className="nav-logo">
          <CricketBatLogo />
          <span>Cricket App</span>
        </NavLink>
        <ul className="nav-menu">
          <li className="nav-item">
            <NavLink to="/matches" className="nav-links">
              Matches
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink to="/teams" className="nav-links">
              Teams
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink to="/players" className="nav-links">
              Players
            </NavLink>
          </li>
        </ul>
        <div className="nav-icon" onClick={handleNavMenuToggle}>
          {navMenuOpen ? <HamburgetMenuClose /> : <HamburgetMenuOpen />}
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
