import React from 'react';
import { motion } from 'framer-motion';

// Define animation variants
const containerVariants = {
  visible: { opacity: 1, transition: { staggerChildren: 0.3 } },
  hidden: { opacity: 0 },
};

const itemVariants = {
  visible: { opacity: 1, y: 0 },
  hidden: { opacity: 0, y: 20 },
};

const Tourna = () => {
  const tournaments = [
    { name: 'T20 Blast', date: 'June 15-30, 2023', teams: 8 },
    { name: 'ODI Championship', date: 'July 10-25, 2023', teams: 10 },
    { name: 'Test Series', date: 'August 5-30, 2023', teams: 4 },
  ];

  return (
    <section className="text-gray-600 body-font">
      <div className="container px-5 py-24 mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="flex flex-col text-center w-full mb-20"
        >
          <h2 className="text-2xl font-medium title-font mb-4 text-gray-900">Featured Tournaments</h2>
          <p className="lg:w-2/3 mx-auto leading-relaxed text-base">
            Check out our upcoming cricket tournaments and join the excitement on the field!
          </p>
        </motion.div>
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="flex flex-wrap -m-4"
        >
          {tournaments.map((tournament, index) => (
            <motion.div key={index} variants={itemVariants} className="p-4 md:w-1/3">
              <motion.div
                whileHover={{ scale: 1.05, boxShadow: '0px 4px 20px rgba(0, 0, 0, 0.1)' }}
                className="flex rounded-lg h-full bg-gray-100 p-8 flex-col transition-all duration-200"
              >
                <div className="flex items-center mb-3">
                  <h2 className="text-gray-900 text-lg title-font font-medium">{tournament.name}</h2>
                </div>
                <div className="flex-grow">
                  <p className="leading-relaxed text-base">{tournament.date}</p>
                  <p className="leading-relaxed text-base">{tournament.teams} Teams</p>
                  <motion.a
                    whileHover={{ x: 5 }}
                    className="mt-3 text-green-500 inline-flex items-center cursor-pointer"
                  >
                    Learn More
                    <svg
                      fill="none"
                      stroke="currentColor"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      className="w-4 h-4 ml-2"
                      viewBox="0 0 24 24"
                    >
                      <path d="M5 12h14M12 5l7 7-7 7"></path>
                    </svg>
                  </motion.a>
                </div>
              </motion.div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Tourna;
