import React, { useState, useEffect } from 'react';
import ScoreCard from '../HomeCompo/ScoreCard';
import PlayerList from '../HomeCompo/PlayerList';

export default function ClientPanel() {
  const [scoreData, setScoreData] = useState({
    homeTeam: 'Red Dragons',
    awayTeam: 'Blue Tigers',
    homeScore: 0,
    homeWickets: 0,
    awayScore: 0,
    awayWickets: 0,
    overs: 0,
    homePlayers: [],
    awayPlayers: [],
    homeBowlers: [],
    awayBowlers: []
  });

  const handlePlayerChange = (team, index, field, value) => {
    setScoreData(prevState => {
      const updatedPlayers = [...prevState[team]];
      updatedPlayers[index] = {
        ...updatedPlayers[index],
        [field]: value
      };
      return {
        ...prevState,
        [team]: updatedPlayers
      };
    });
  };

  const handleAddPlayer = (team) => {
    setScoreData(prevState => ({
      ...prevState,
      [team]: [...prevState[team], { name: 'New Player', runs: 0, balls: 0, fours: 0, sixes: 0 }]
    }));
  };

  const handleAddBowler = (team) => {
    setScoreData(prevState => ({
      ...prevState,
      [team]: [...prevState[team], { name: 'New Bowler', overs: 0, maiden: 0, runsGiven: 0, wickets: 0 }]
    }));
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold mb-8">Cricket Live Score</h1>
      <div className="max-w-4xl mx-auto">
        <ScoreCard
          homeTeam={scoreData.homeTeam}
          awayTeam={scoreData.awayTeam}
          homeScore={scoreData.homeScore}
          homeWickets={scoreData.homeWickets}
          awayScore={scoreData.awayScore}
          awayWickets={scoreData.awayWickets}
          overs={scoreData.overs}
        />
        <h2 className="text-2xl font-bold mb-4">{scoreData.homeTeam} Players</h2>
        <PlayerList
          players={scoreData.homePlayers}
          onPlayerChange={(index, field, value) => handlePlayerChange('homePlayers', index, field, value)}
        />
        <button
          className="bg-blue-500 text-white py-2 px-4 rounded mb-4"
          onClick={() => handleAddPlayer('homePlayers')}
        >
          Add Player
        </button>

        <h2 className="text-2xl font-bold mb-4">{scoreData.awayTeam} Players</h2>
        <PlayerList
          players={scoreData.awayPlayers}
          onPlayerChange={(index, field, value) => handlePlayerChange('awayPlayers', index, field, value)}
        />
        <button
          className="bg-blue-500 text-white py-2 px-4 rounded mb-4"
          onClick={() => handleAddPlayer('awayPlayers')}
        >
          Add Player
        </button>

        <h2 className="text-2xl font-bold mb-4">{scoreData.homeTeam} Bowlers</h2>
        <PlayerList
          players={scoreData.homeBowlers}
          isBowler
          onPlayerChange={(index, field, value) => handlePlayerChange('homeBowlers', index, field, value)}
        />
        <button
          className="bg-green-500 text-white py-2 px-4 rounded mb-4"
          onClick={() => handleAddBowler('homeBowlers')}
        >
          Add Bowler
        </button>

        <h2 className="text-2xl font-bold mb-4">{scoreData.awayTeam} Bowlers</h2>
        <PlayerList
          players={scoreData.awayBowlers}
          isBowler
          onPlayerChange={(index, field, value) => handlePlayerChange('awayBowlers', index, field, value)}
        />
        <button
          className="bg-green-500 text-white py-2 px-4 rounded mb-4"
          onClick={() => handleAddBowler('awayBowlers')}
        >
          Add Bowler
        </button>
      </div>
    </div>
  );
}
