import React, { useState } from 'react';
import { Calendar, Clock } from 'lucide-react';

// Button Component
export const Button = ({ variant = 'default', className, ...props }) => {
  const variantClass = variant === 'secondary' ? 'bg-gray-500 text-white' : 'bg-blue-500 text-white';
  return (
    <button className={`${variantClass} px-4 py-2 rounded ${className}`} {...props} />
  );
};

// Card Components
export const Card = ({ className, children, ...props }) => (
  <div className={`p-4 border rounded shadow ${className}`} {...props}>
    {children}
  </div>
);

export const CardHeader = ({ children }) => (
  <div className="mb-2">
    {children}
  </div>
);

export const CardTitle = ({ children, className }) => (
  <h2 className={`text-xl font-bold ${className}`}>
    {children}
  </h2>
);

export const CardDescription = ({ children, className }) => (
  <p className={`text-sm ${className}`}>
    {children}
  </p>
);

export const CardContent = ({ children }) => (
  <div className="mb-4">
    {children}
  </div>
);

export const CardFooter = ({ children }) => (
  <div className="mt-4">
    {children}
  </div>
);

// Cricket Animations Components
const CricketBall = () => (
  <div className="cricket-ball"></div>
);

const CricketBat = () => (
  <div className="cricket-bat"></div>
);

const CricketStumps = () => (
  <div className="cricket-stumps">
    <div className="stump"></div>
    <div className="stump"></div>
    <div className="stump"></div>
    <div className="bails"></div>
  </div>
);

// Tournament Card Component
const TournamentCard = ({ tournament, index }) => {
  const [isHovered, setIsHovered] = useState(false);
  const isCompleted = tournament.completed;
  const cardStyle = isCompleted ? 'bg-gray-200' : 'bg-white';
  const textStyle = isCompleted ? 'text-gray-600' : 'text-black';

  return (
    <Card 
      className={`${cardStyle} mb-4 transition-all duration-300 ease-in-out transform hover:scale-105 hover:shadow-lg relative overflow-hidden`}
      style={{ animationDelay: `${index * 100}ms` }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <CardHeader>
        <CardTitle className={`${textStyle} text-xl font-bold`}>{tournament.name}</CardTitle>
        <CardDescription className={`${textStyle} text-sm`}>Format: {tournament.format}</CardDescription>
      </CardHeader>
      <CardContent>
        <div className={`${textStyle} flex items-center mb-2 transition-all duration-300 ${isHovered ? 'translate-x-2' : ''}`}>
          <Calendar className="mr-2" size={16} />
          <span className="text-sm">{tournament.startDate} to {tournament.endDate}</span>
        </div>
        <div className={`${textStyle} flex items-center transition-all duration-300 ${isHovered ? 'translate-x-2' : ''}`}>
          <Clock className="mr-2" size={16} />
          <span className="text-sm">{isCompleted ? 'Completed' : 'Upcoming'}</span>
        </div>
      </CardContent>
      <CardFooter>
        <Button 
          disabled={isCompleted} 
          variant={isCompleted ? 'secondary' : 'default'}
          className={`transition-all duration-300 ${isHovered && !isCompleted ? 'animate-pulse' : ''}`}
        >
          {isCompleted ? 'Completed' : 'Register'}
        </Button>
      </CardFooter>
      {isHovered && (
        <>
          <CricketBall />
          <CricketBat />
        </>
      )}
      {isCompleted && <CricketStumps />}
    </Card>
  );
};

// Main Page Component
const CricketTournamentsPage = () => {
  const tournaments = [
    { id: 1, name: "ICC T20 World Cup 2024", format: "T20", startDate: "2024-06-01", endDate: "2024-06-28", completed: false },
    { id: 2, name: "ICC Cricket World Cup 2023", format: "ODI", startDate: "2023-10-05", endDate: "2023-11-19", completed: true },
    { id: 3, name: "The Ashes 2023", format: "Test", startDate: "2023-06-16", endDate: "2023-07-31", completed: true },
    { id: 4, name: "IPL 2025", format: "T20", startDate: "2025-03-23", endDate: "2025-05-29", completed: false },
  ];

  return (
    <div className="container mx-auto px-4 py-8 relative">
      <h1 className="text-3xl font-bold mb-6 animate-fade-in">Cricket Tournaments</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tournaments.map((tournament, index) => (
          <div key={tournament.id} className="animate-fade-in-up" style={{ animationDelay: `${index * 150}ms` }}>
            <TournamentCard tournament={tournament} index={index} />
          </div>
        ))}
      </div>
      <div className="cricket-field"></div>
    </div>
  );
};

export default CricketTournamentsPage;
