"use client"

import React, { useRef } from "react"
import { motion, useScroll, useTransform } from "framer-motion"
import Link from "next/link"

// Define animation variants
const containerVariants = {
  hidden: { opacity: 0 },
  visible: { 
    opacity: 1, 
    transition: { 
      staggerChildren: 0.2,
      delayChildren: 0.3,
      duration: 1.2, 
      type: "spring" 
    } 
  },
}

const itemVariants = {
  hidden: { opacity: 0, y: 30 },
  visible: { 
    opacity: 1, 
    y: 0, 
    transition: { type: 'spring', stiffness: 150, damping: 20 } 
  },
}

const textRevealVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { 
    opacity: 1, 
    y: 0, 
    transition: { duration: 0.8, ease: [0.6, -0.05, 0.01, 0.99] } 
  },
}

const buttonVariants = {
  hover: { 
    scale: 1.05, 
    transition: { duration: 0.3 },
    boxShadow: "0px 5px 15px rgba(0, 0, 0, 0.1)"
  },
  tap: { scale: 0.95 }
}

export default function HeroSection() {
  const ref = useRef(null)
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start start", "end start"]
  })

  const backgroundY = useTransform(scrollYProgress, [0, 1], ["0%", "30%"])
  const textY = useTransform(scrollYProgress, [0, 1], ["0%", "100%"])

  return (
    <motion.section
      ref={ref}
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className="relative text-gray-600 body-font flex items-center justify-center h-screen overflow-hidden"
    >
      {/* Background Image with Parallax */}
      <motion.div 
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage: "url('https://www.fabhotels.com/blog/wp-content/uploads/2023/04/adelaide-oval.jpg')",
          y: backgroundY
        }}
      />

      {/* Overlay */}
      <motion.div 
        className="absolute inset-0 bg-black bg-opacity-50" 
        initial={{ opacity: 0 }} 
        animate={{ opacity: 0.5 }} 
        transition={{ duration: 1 }} 
      />

      {/* Content */}
      <div className="relative container mx-auto px-4 py-4 md:py-8 flex flex-col items-center text-center z-10">
        <motion.div variants={itemVariants} className="flex flex-col items-center mb-16 space-y-6">
          {/* Title Animation */}
          <motion.h1 
            variants={textRevealVariants} 
            className="title-font text-4xl sm:text-5xl md:text-6xl font-bold text-white leading-tight"
            style={{ y: textY }}
          >
            Experience the Thrill of Cricket
            <br className="hidden lg:inline-block" />
            with CRICKO Organization
          </motion.h1>

          {/* Description Animation */}
          <motion.p
            variants={textRevealVariants}
            className="text-lg sm:text-xl md:text-2xl mb-8 leading-relaxed text-gray-200 max-w-3xl"
            style={{ y: textY }}
          >
            Join us for exciting cricket tournaments in various formats. Whether you're a player or a fan, we've got something for everyone. Discover the passion, skill, and excitement of cricket with CRICKO Organization.
          </motion.p>

          {/* Buttons */}
          <motion.div variants={itemVariants} className="flex justify-center space-x-6">
            <Link href="/tournaments" passHref>
              <motion.button
                variants={buttonVariants}
                whileHover="hover"
                whileTap="tap"
                className="inline-flex items-center justify-center text-white bg-blue-600 border-0 py-3 px-8 text-lg focus:outline-none hover:bg-blue-700 rounded-full transition-colors duration-300 shadow-lg"
              >
                Explore Tournaments
              </motion.button>
            </Link>

            <Link href="/about" passHref>
              <motion.button
                variants={buttonVariants}
                whileHover="hover"
                whileTap="tap"
                className="inline-flex items-center justify-center text-blue-600 bg-white border-2 border-blue-600 py-3 px-8 text-lg focus:outline-none hover:bg-gray-100 rounded-full transition-colors duration-300 shadow-lg"
              >
                About Us
              </motion.button>
            </Link>
          </motion.div>
        </motion.div>
      </div>
    </motion.section>
  )
}
