import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// Example icon URLs (replace these with your actual image URLs)
const roleIcons = {
  Batsman: 'https://i.pinimg.com/originals/ce/51/6d/ce516d3914ae36a4b1f4165cd6a6b780.png',
  Bowler: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRC8yOAKTZ1QV18IHC_bwuk0QNzzoes-b4iZA&s',
  Wicketkeeper: 'https://png.pngtree.com/png-clipart/20221213/ourmid/pngtree-cricket-stamps-png-image_6521913.png'
};

const TeamFormPage2 = () => {
  const [formData, setFormData] = useState({
    player1: "",
    player2: "",
    player3: "",
    player4: "",
    player5: "",
    player6: "",
    player7: "",
    player8: "",
    player9: "",
    player10: ""
  });

  const [roles, setRoles] = useState({
    player1Role: "",
    player2Role: "",
    player3Role: "",
    player4Role: "",
    player5Role: "",
    player6Role: "",
    player7Role: "",
    player8Role: "",
    player9Role: "",
    player10Role: ""
  });

  const navigate = useNavigate();

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem("teamFormData"));
    if (storedData) {
      setFormData((prev) => ({ ...prev, ...storedData }));
    } else {
      navigate("/register");
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleRoleChange = (index, role) => {
    setRoles({
      ...roles,
      [`player${index + 1}Role`]: role,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const completeFormData = JSON.parse(localStorage.getItem("teamFormData"));
    localStorage.setItem("teamFormData", JSON.stringify({ ...completeFormData, ...formData, roles }));

    localStorage.removeItem("teamFormData");
    navigate("/success");
  };

  return (
    <div className="container mx-auto p-6">
      <div className="max-w-md mx-auto">
        <h2 className="text-2xl font-bold mb-6 text-center">Add Players</h2>
        <form onSubmit={handleSubmit} className="space-y-5">
          {[...Array(10)].map((_, index) => (
            <div key={index} className="flex items-center justify-between">
              <div className="flex-1">
                <label className="block text-lg font-medium text-gray-700">Player {index + 1}</label>
                <input
                  type="text"
                  name={`player${index + 1}`}
                  value={formData[`player${index + 1}`]}
                  onChange={handleChange}
                  className="mt-1 block w-full p-3 border border-gray-300 rounded-md text-lg" // Increased input size
                  required
                />
              </div>
              <div className="flex items-center ml-4">
                <label className="block text-lg font-medium text-gray-700 mr-2">Role:</label>
                <div className="flex">
                  {["Batsman", "Bowler", "Wicketkeeper"].map((role) => (
                    <div key={role} className="mr-3 flex items-center">
                      <input
                        type="radio"
                        name={`role${index + 1}`}
                        value={role}
                        checked={roles[`player${index + 1}Role`] === role}
                        onChange={() => handleRoleChange(index, role)}
                        className="mr-2"
                      />
                      <img
                        src={roleIcons[role]}
                        alt={`${role} Icon`}
                        className="w-8 h-8" // Standardized icon size
                      />
                      <span className="text-lg ml-1">{role}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}

          <button
            type="submit"
            className="bg-blue-500 text-white px-6 py-3 rounded-md hover:bg-blue-600 text-lg w-full" // Full width button
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default TeamFormPage2;
