import React from 'react';
import Image from 'next/image';
// import { Button } from "@/components/ui/button";
import { Button } from '../ui/button';

// Dummy data for matches
const matches = [
  { id: 1, team1: "Red Dragons", team2: "Blue Tigers", date: "2023-06-15", time: "15:00" },
  { id: 2, team1: "Green Eagles", team2: "Yellow Lions", date: "2023-06-16", time: "18:30" },
  { id: 3, team1: "Purple Panthers", team2: "Orange Owls", date: "2023-06-17", time: "20:00" },
  { id: 4, team1: "Silver Sharks", team2: "Gold Gazelles", date: "2023-06-18", time: "16:45" },
  { id: 5, team1: "Black Bears", team2: "White Wolves", date: "2023-06-19", time: "19:15" },
  { id: 6, team1: "Cyan Cheetahs", team2: "Magenta Monkeys", date: "2023-06-20", time: "17:30" },
];

export default function Blog() {
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-8">Upcoming Matches</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {matches.map((match) => (
          <div key={match.id} className="bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-300 hover:scale-105">
            <div className="p-4 bg-gradient-to-r from-blue-500 to-purple-600 text-white">
              <div className="flex justify-between items-center">
                <div className="text-sm">{match.date}</div>
                <div className="text-sm font-semibold">{match.time}</div>
              </div>
            </div>
            <div className="p-4">
              <div className="flex justify-between items-center mb-4">
                <div className="flex items-center">
                  <Image
                    src={`/placeholder.svg?height=50&width=50&text=${match.team1[0]}`}
                    alt={`${match.team1} logo`}
                    width={50}
                    height={50}
                    className="rounded-full"
                  />
                  <span className="ml-2 font-semibold">{match.team1}</span>
                </div>
                <div className="text-xl font-bold">VS</div>
                <div className="flex items-center">
                  <span className="mr-2 font-semibold">{match.team2}</span>
                  <Image
                    src={`/placeholder.svg?height=50&width=50&text=${match.team2[0]}`}
                    alt={`${match.team2} logo`}
                    width={50}
                    height={50}
                    className="rounded-full"
                  />
                </div>
              </div>
              <Button className="w-full bg-gradient-to-r from-green-400 to-blue-500 hover:from-green-500 hover:to-blue-600 text-white font-semibold py-2 px-4 rounded-full transition-all duration-300 transform hover:scale-105">
                View Scorecard
              </Button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
