import React, { useState } from 'react';
import ScoreCard from '../HomeCompo/ScoreCard'; // Assuming ScoreCard component is used elsewhere

export default function ClientPanel() {
  const [scoreData, setScoreData] = useState({
    homeTeam: 'Red Dragons',
    awayTeam: 'Blue Tigers',
    homePlayers: Array(11).fill({ name: '', runs: 0, balls: 0, fours: 0, sixes: 0 }),
    awayPlayers: Array(11).fill({ name: '', runs: 0, balls: 0, fours: 0, sixes: 0 }),
    homeBowlers: Array(11).fill({ name: '', overs: 0, maiden: 0, runsGiven: 0, wickets: 0 }),
    awayBowlers: Array(11).fill({ name: '', overs: 0, maiden: 0, runsGiven: 0, wickets: 0 }),
  });

  // Handle input changes for batsman data (runs, balls, sixes, fours)
  const handleBatsmanChange = (team, index, field, value) => {
    setScoreData(prevState => {
      const updatedPlayers = [...prevState[team]];
      updatedPlayers[index] = {
        ...updatedPlayers[index],
        [field]: value,
      };
      return {
        ...prevState,
        [team]: updatedPlayers
      };
    });
  };

  // Handle input changes for bowler data (overs, maiden, runsGiven, wickets)
  const handleBowlerChange = (team, index, field, value) => {
    setScoreData(prevState => {
      const updatedBowlers = [...prevState[team]];
      updatedBowlers[index] = {
        ...updatedBowlers[index],
        [field]: value,
      };
      return {
        ...prevState,
        [team]: updatedBowlers
      };
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold mb-8">Cricket Live Score</h1>
      <div className="max-w-4xl mx-auto">
        <ScoreCard homeTeam={scoreData.homeTeam} awayTeam={scoreData.awayTeam} />
        
        {/* Home Team Batsmen */}
        <h2 className="text-2xl font-bold mb-4">{scoreData.homeTeam} Batsmen</h2>
        {scoreData.homePlayers.map((player, index) => (
          <div key={index} className="mb-4 p-2 border">
            <h3 className="text-xl font-semibold">Batsman {index + 1}</h3>
            <label>Name:</label>
            <input
              type="text"
              value={player.name}
              onChange={(e) => handleBatsmanChange('homePlayers', index, 'name', e.target.value)}
              className="border p-2 ml-2 mb-2"
            />
            <label>Runs:</label>
            <input
              type="number"
              value={player.runs}
              onChange={(e) => handleBatsmanChange('homePlayers', index, 'runs', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Balls:</label>
            <input
              type="number"
              value={player.balls}
              onChange={(e) => handleBatsmanChange('homePlayers', index, 'balls', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Fours:</label>
            <input
              type="number"
              value={player.fours}
              onChange={(e) => handleBatsmanChange('homePlayers', index, 'fours', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Sixes:</label>
            <input
              type="number"
              value={player.sixes}
              onChange={(e) => handleBatsmanChange('homePlayers', index, 'sixes', Number(e.target.value))}
              className="border p-2 ml-2"
            />
          </div>
        ))}

        {/* Home Team Bowlers */}
        <h2 className="text-2xl font-bold mb-4">{scoreData.homeTeam} Bowlers</h2>
        {scoreData.homeBowlers.map((bowler, index) => (
          <div key={index} className="mb-4 p-2 border">
            <h3 className="text-xl font-semibold">Bowler {index + 1}</h3>
            <label>Name:</label>
            <input
              type="text"
              value={bowler.name}
              onChange={(e) => handleBowlerChange('homeBowlers', index, 'name', e.target.value)}
              className="border p-2 ml-2 mb-2"
            />
            <label>Overs:</label>
            <input
              type="number"
              value={bowler.overs}
              onChange={(e) => handleBowlerChange('homeBowlers', index, 'overs', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Maiden:</label>
            <input
              type="number"
              value={bowler.maiden}
              onChange={(e) => handleBowlerChange('homeBowlers', index, 'maiden', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Runs Given:</label>
            <input
              type="number"
              value={bowler.runsGiven}
              onChange={(e) => handleBowlerChange('homeBowlers', index, 'runsGiven', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Wickets:</label>
            <input
              type="number"
              value={bowler.wickets}
              onChange={(e) => handleBowlerChange('homeBowlers', index, 'wickets', Number(e.target.value))}
              className="border p-2 ml-2"
            />
          </div>
        ))}

        {/* Similar UI for Away Team */}
        <h2 className="text-2xl font-bold mb-4">{scoreData.awayTeam} Batsmen</h2>
        {scoreData.awayPlayers.map((player, index) => (
          <div key={index} className="mb-4 p-2 border">
            <h3 className="text-xl font-semibold">Batsman {index + 1}</h3>
            <label>Name:</label>
            <input
              type="text"
              value={player.name}
              onChange={(e) => handleBatsmanChange('awayPlayers', index, 'name', e.target.value)}
              className="border p-2 ml-2 mb-2"
            />
            <label>Runs:</label>
            <input
              type="number"
              value={player.runs}
              onChange={(e) => handleBatsmanChange('awayPlayers', index, 'runs', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Balls:</label>
            <input
              type="number"
              value={player.balls}
              onChange={(e) => handleBatsmanChange('awayPlayers', index, 'balls', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Fours:</label>
            <input
              type="number"
              value={player.fours}
              onChange={(e) => handleBatsmanChange('awayPlayers', index, 'fours', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Sixes:</label>
            <input
              type="number"
              value={player.sixes}
              onChange={(e) => handleBatsmanChange('awayPlayers', index, 'sixes', Number(e.target.value))}
              className="border p-2 ml-2"
            />
          </div>
        ))}

        <h2 className="text-2xl font-bold mb-4">{scoreData.awayTeam} Bowlers</h2>
        {scoreData.awayBowlers.map((bowler, index) => (
          <div key={index} className="mb-4 p-2 border">
            <h3 className="text-xl font-semibold">Bowler {index + 1}</h3>
            <label>Name:</label>
            <input
              type="text"
              value={bowler.name}
              onChange={(e) => handleBowlerChange('awayBowlers', index, 'name', e.target.value)}
              className="border p-2 ml-2 mb-2"
            />
            <label>Overs:</label>
            <input
              type="number"
              value={bowler.overs}
              onChange={(e) => handleBowlerChange('awayBowlers', index, 'overs', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Maiden:</label>
            <input
              type="number"
              value={bowler.maiden}
              onChange={(e) => handleBowlerChange('awayBowlers', index, 'maiden', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Runs Given:</label>
            <input
              type="number"
              value={bowler.runsGiven}
              onChange={(e) => handleBowlerChange('awayBowlers', index, 'runsGiven', Number(e.target.value))}
              className="border p-2 ml-2"
            />
            <label>Wickets:</label>
            <input
              type="number"
              value={bowler.wickets}
              onChange={(e) => handleBowlerChange('awayBowlers', index, 'wickets', Number(e.target.value))}
              className="border p-2 ml-2"
            />
          </div>
        ))}
      </div>
    </div>
  );
}
