import React from 'react';
import { Button } from '../ui/button';

// Dummy data for matches
const matches = [
  { id: 1, team1: "Red Dragons", team2: "Blue Tigers", date: "2023-06-15", time: "15:00" },
  { id: 2, team1: "Green Eagles", team2: "Yellow Lions", date: "2023-06-16", time: "18:30" },
  { id: 3, team1: "Purple Panthers", team2: "Orange Owls", date: "2023-06-17", time: "20:00" },
  { id: 4, team1: "Silver Sharks", team2: "Gold Gazelles", date: "2023-06-18", time: "16:45" },
  { id: 5, team1: "Black Bears", team2: "White Wolves", date: "2023-06-19", time: "19:15" },
  { id: 6, team1: "Cyan Cheetahs", team2: "Magenta Monkeys", date: "2023-06-20", time: "17:30" },
];

// Mapping for team logos
const teamLogos = {
  "Red Dragons": "https://bcassetcdn.com/public/blog/wp-content/uploads/2021/10/14194010/abstract-cricket-player-by-eightylogos-brandcrowd.png",
  "Blue Tigers": "https://i.pinimg.com/236x/96/1b/67/961b672143c8f75e7793e6dc2ced7af2.jpg",
  "Green Eagles": "https://png.pngtree.com/png-clipart/20230524/original/pngtree-cricket-logo-free-vector-png-image_9169643.png",
  "Yellow Lions": "/logos/yellow-lions.svg",
  "Purple Panthers": "/logos/purple-panthers.svg",
  "Orange Owls": "/logos/orange-owls.svg",
  "Silver Sharks": "/logos/silver-sharks.svg",
  "Gold Gazelles": "/logos/gold-gazelles.svg",
  "Black Bears": "/logos/black-bears.svg",
  "White Wolves": "/logos/white-wolves.svg",
  "Cyan Cheetahs": "/logos/cyan-cheetahs.svg",
  "Magenta Monkeys": "/logos/magenta-monkeys.svg",
};

// Mapping for team scorecards (hypothetical URLs)
const scorecardLinks = {
  "Red Dragons": "/scorecard/red-dragons",
  "Blue Tigers": "/scorecard/blue-tigers",
  "Green Eagles": "/scorecard/green-eagles",
  "Yellow Lions": "/scorecard/yellow-lions",
  "Purple Panthers": "/scorecard/purple-panthers",
  "Orange Owls": "/scorecard/orange-owls",
  "Silver Sharks": "/scorecard/silver-sharks",
  "Gold Gazelles": "/scorecard/gold-gazelles",
  "Black Bears": "/scorecard/black-bears",
  "White Wolves": "/scorecard/white-wolves",
  "Cyan Cheetahs": "/scorecard/cyan-cheetahs",
  "Magenta Monkeys": "/scorecard/magenta-monkeys",
};

function Blog() {
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold text-center mb-6">Upcoming Matches</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {matches.map((match) => (
          <div key={match.id} className="bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-300 hover:scale-105">
            <div className="p-3 bg-gradient-to-r from-blue-400 to-purple-500 text-white">
              <div className="flex justify-between items-center">
                <div className="text-xs">{match.date}</div>
                <div className="text-xs font-semibold">{match.time}</div>
              </div>
            </div>
            <div className="p-3">
              <div className="flex justify-between items-center mb-3">
                <div className="flex items-center">
                  <img
                    src={teamLogos[match.team1]}
                    alt={`${match.team1} logo`}
                    width={40}
                    height={40}
                    className="rounded-full"
                  />
                  <span className="ml-2 text-sm font-medium">{match.team1}</span>
                </div>
                <div className="text-lg font-bold">VS</div>
                <div className="flex items-center">
                  <span className="mr-2 text-sm font-medium">{match.team2}</span>
                  <img
                    src={teamLogos[match.team2]}
                    alt={`${match.team2} logo`}
                    width={40}
                    height={40}
                    className="rounded-full"
                  />
                </div>
              </div>
              <Button
                className="w-full bg-gradient-to-r from-green-400 to-blue-500 hover:from-green-500 hover:to-blue-600 text-white text-sm py-2 rounded-full transition-all duration-300 transform hover:scale-105"
                onClick={() => window.location.href = scorecardLinks[match.team1]}>
                View Scorecard
              </Button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Blog;
