import React from "react";
import { CardBody, CardContainer, CardItem } from "../ui/3d-card";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

const FeaturedTeams=()=> {
  const teams = [
    {
      name: "Team A",
      description: "Description for Team A",
      logo: "https://www.shutterstock.com/image-vector/cricket-sport-team-club-logo-600nw-2156607285.jpg",
      points: 120,
    },
    {
      name: "Team B",
      description: "Description for Team B",
      logo: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRfObvcZio7M90JvyCWytrh7McQu_m8J0j1Bg&s",
      points: 95,
    },
    {
      name: "Team C",
      description: "Description for Team C",
      logo: "https://t4.ftcdn.net/jpg/04/26/13/71/360_F_426137121_u2zwsANOFCMcAZyAKAA9jJu1GCWxWaLG.jpg",
      points: 88,
    },
    {
      name: "Team D",
      description: "Description for Team D",
      logo: "https://w7.pngwing.com/pngs/812/694/png-transparent-logo-sports-association-cricket-team-cricket-purple-text-sport.png",
      points: 110,
    },
  ];

  return (
    <section className="py-8 px-4 sm:px-6 lg:px-12">
      <h2 className="font-semibold text-center text-[#316685] mb-6 text-3xl">
        Featured Teams
      </h2>
      <div className="flex flex-wrap justify-center gap-6">
        {teams.map((team, index) => (
          <CardContainer key={team.name} className="inter-var">
            <CardBody className="bg-gray-50 relative group/card dark:hover:shadow-2xl dark:hover:shadow-emerald-500/[0.1] dark:bg-black dark:border-white/[0.2] border-black/[0.1] w-auto sm:w-[20rem] h-auto rounded-xl p-6 border">
              <CardItem
                translateZ="50"
                className="text-xl font-bold text-neutral-600 dark:text-white"
              >
                {team.name}
              </CardItem>
              <CardItem
                as="p"
                translateZ="60"
                className="text-neutral-500 text-sm mt-2 dark:text-neutral-300"
              >
                {team.description}
              </CardItem>
              <CardItem translateZ="100" className="w-full mt-4">
                <img
                  src={team.logo}
                  height="1000"
                  width="1000"
                  className="h-40 w-full object-cover rounded-xl group-hover/card:shadow-xl"
                  alt={`${team.name} Logo`}
                />
              </CardItem>
              <CardItem
                translateZ="70"
                className="text-blue-500 text-sm mt-4 font-semibold"
              >
                Total Points: {team.points}
              </CardItem>
              <div className="flex justify-between items-center mt-4">
                <CardItem
                  translateZ={20}
                  as={Link}
                  to="https://example.com"
                  target="__blank"
                  className="px-4 py-2 rounded-xl text-xs font-normal dark:text-white"
                >
                  Learn More â†’
                </CardItem>
                <CardItem
                  translateZ={20}
                  as="button"
                  className="px-4 py-2 rounded-xl bg-black dark:bg-white dark:text-black text-white text-xs font-bold"
                >
                  Join Team
                </CardItem>
              </div>
            </CardBody>
          </CardContainer>
        ))}
      </div>
    </section>
  );
}
export default FeaturedTeams;
