import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

// Define animation variants
const containerVariants = {
  visible: {
    opacity: 1,
    y: 0,
    transition: { delay: 0.3, staggerChildren: 0.2, duration: 1.2, type: "spring" },
  },
  hidden: { opacity: 0, y: -50 },
};

const itemVariants = {
  visible: {
    opacity: 1,
    y: 0,
    transition: { type: "spring", stiffness: 150, damping: 20 },
  },
  hidden: { opacity: 0, y: 30 },
};

const textVariants = {
  visible: { opacity: 1, x: 0, transition: { duration: 1, type: "tween" } },
  hidden: { opacity: 0, x: -50 },
};

const buttonVariants = {
  hover: { scale: 1.1, transition: { yoyo: Infinity, duration: 0.4 } },
};

const HeroSection = () => {
  return (
    <motion.section
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className="relative text-gray-600 body-font flex items-center justify-center bg-cover bg-center h-screen md:h-90vh lg:h-4/5 xl:h-5/6"
      style={{
        backgroundImage: "url('https://www.fabhotels.com/blog/wp-content/uploads/2023/04/adelaide-oval.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      {/* Overlay */}
      <motion.div
        className="absolute inset-0 bg-black bg-opacity-50"
        initial={{ opacity: 0 }}
        animate={{ opacity: 0.5 }}
        transition={{ duration: 1 }}
      ></motion.div>

      {/* Content */}
      <div className="relative container mx-auto px-4 py-4 md:py-8 flex flex-col items-center text-center">
        
        {/* Headline and Description */}
        <motion.div variants={itemVariants} className="flex flex-col items-center mb-16">
          {/* Title Animation */}
          <motion.h1
            variants={textVariants}
            className="title-font text-2xl sm:text-3xl md:text-4xl lg:text-5xl mb-4 font-medium text-white"
          >
            Experience the Thrill of Cricket
            <br className="hidden lg:inline-block" />
            with CRICKO Organization
          </motion.h1>

          {/* Description Animation */}
          <motion.p
            variants={textVariants}
            className="text-base sm:text-lg md:text-xl mb-8 leading-relaxed text-white"
          >
            Join us for exciting cricket tournaments in various formats. Whether you're a player or a fan, we've got something for everyone. Discover the passion, skill, and excitement of cricket with CRICKO Organization.
          </motion.p>

          {/* Buttons */}
          <motion.div variants={itemVariants} className="flex justify-center space-x-4">
            {/* Explore Tournaments Button */}
            <Link to="/about">
              <motion.button
                whileHover="hover"
                variants={buttonVariants}
                className="inline-flex text-white bg-custom-blue border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-custom-blue/80 rounded"
              >
                Explore Tournaments
              </motion.button>
            </Link>

            {/* About Us Button */}
            <Link to="/contact">
              <motion.button
                whileHover="hover"
                variants={buttonVariants}
                className="inline-flex text-gray-700 bg-gray-100 border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-gray-200 rounded"
              >
                About Us
              </motion.button>
            </Link>
          </motion.div>
        </motion.div>
      </div>

      {/* Add a modern animation: a scrolling effect on the background image */}
      <motion.div
        className="absolute inset-0"
        initial={{ y: 0 }}
        animate={{ y: -200 }}
        transition={{ duration: 10, repeat: Infinity }}
      ></motion.div>
    </motion.section>
  );
};

export default HeroSection;
